import {useEffect, useState} from 'react'

import Input from "./Input"
import SubmitButton from "./SubmitButton"

function ProductForm(handleSubmit ,productData) {

    const [Product, setProduct] = useState([productData])

    const submit = (e) => {
        e.preventDefault()
        handleSubmit(Product)
        
    }

    function handleChange(e) {
        setProduct({ ...Product, [e.target.name] : e.target.value })

        console.log(Product)
    }

    return(
        <form onSubmit={submit}>
            <Input type='text' text='Nome do produto' name='name' placeholder='Insira o nome do produto' handleOnChange={handleChange}/>
            <Input type='number' text='Preço do produto' name='price' placeholder='Insira o Preço do produto'/>
            <Input type='number' text='Código do produto' name='code' placeholder='Insira o Código do produto'/>
            <Input type='number' text='Estoque mínimo do produto' name='minimum_stock' placeholder='Insira o Estoque mínimo do produto'/>
            <Input type='text' text='Qualidade do produto' name='quality' placeholder='Insira a qualidade do produto'/>
            <Input type='text' text='Validade do produto' name='validate' placeholder='Insira a validade do produto'/>
            <SubmitButton text='Enviar Produto'/>
        </form>
    )
}

export default ProductForm
